const array = [6.1, 4.2, 6.3];
const grouped = array.group(num => Math.floor(num));
console.log(grouped);
  // Output: { '4': [4.2], '6': [6.1, 6.3] }

const array = [6.1, 4.2, 6.3];
const groupedMap = array.groupToMap(num => Math.floor(num));
console.log(groupedMap);
  // Output: Map { 4 => [4.2], 6 => [6.1, 6.3] }

